import value from '../../../../assets/scss/_themes-vars.scss';
import{useState,useEffect} from "react";
import axios from 'axios';

const Default = () => {
const[count,setCount]= useState(0);
const[count1,setCount1]= useState(0);
const[counts,setCounts]= useState(0);

useEffect(() => {
    fetch('http://localhost:5000/outofstock')
      .then((res) => res.json())
      .then((data) => {
        setCounts(data[0].total_out_of_stock);
      });
  }, []);

  useEffect(() => {
    fetch('http://localhost:5000/data/count')
      .then((res) => res.json())
      .then((data) => {
        setCount1(data.count1);
      });
  }, []);


  const fetchTest =  () => {

      axios.get("http://localhost:5000/count")
      .then((res)=>setCount(JSON.stringify(res.data[0]['count'])))
      .catch((err)=>console.log(err))
      ;

  };


  useEffect(()=>{

    fetchTest();



  },[]);



 return {

    height: 228,
    type: 'pie',
    options: {
        dataLabels: {
            enabled: false,
        },
        labels: ['Total Items', 'Incoming', 'Outgoing'],
        legend: {
            show: true,
            position: 'bottom',
            fontFamily: 'inherit',
            labels: {
                colors: 'inherit',
            },
        },
        itemMargin: {
            horizontal: 10,
            vertical: 10,
        },
        colors: [value.error, value.primary, value.info],
    },
    series: [count,count1,counts]
}
};

export default Default;
