import React from "react";
import { useTheme } from "@material-ui/styles";
import Chart from "react-apexcharts";

import {
  Box,
  Card,
  CardContent,
  Grid,
  makeStyles,
  Typography,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  content: {
    padding: 0,
    paddingBottom: "0 !important",
  },
}));

const SalesLineCard = (props) => {
  const classes = useStyles();
  const theme = useTheme();

  const { bgColor, chartData, footerData, icon, title, percentage } = props;

  let footerHtml;
  if (footerData) {
    footerHtml = footerData.map((item, index) => {
      return (
        <Grid item key={index}>
          <Box mt={1} mb={1} p={1}>
            <Grid container direction="column" spacing={1} alignItems="center">
              <Typography variant="h4">{item.value}</Typography>
              <Typography variant="subtitle2" color="secondary">
                {/* {item.label} */}hgjghjg
              </Typography>
            </Grid>
          </Box>
        </Grid>
      );
    });
  }

  return (
    <Card>
      <CardContent className={classes.content}>
        <Box
          color="#fff"
          bgcolor={bgColor ? bgColor : theme.palette.primary.main}
          p={3}
        >
          <Grid container direction="column" spacing={7}>
            <Grid item container justify="space-between" alignItems="center">
              {title && (
                <Grid item>
                  <Typography variant="subtitle1" color="inherit">
                    {/* {title} */}jhgjghj
                  </Typography>
                </Grid>
              )}
              <Grid item>
                <Grid container alignItems="center">
                  {icon && (
                    <Box component="span" mr={2}>
                      {/* {icon} */}jghjghj
                    </Box>
                  )}
                  {percentage && (
                    <Typography variant="subtitle1" color="inherit">
                      {/* {percentage} */}hfghfgh
                    </Typography>
                  )}
                </Grid>
              </Grid>
            </Grid>
            {chartData && (
              <Grid item>
                <Chart {...chartData} />
              </Grid>
            )}
          </Grid>
        </Box>
        {footerData && (
          <Grid container justify="space-around" alignItems="center">
            {footerHtml}
          </Grid>
        )}
      </CardContent>
    </Card>
  );
};

export default SalesLineCard;
